package packageone;

public class IfElseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=9;
		int y=6;
		int z=87;
		if(x>y && x>z)
		{
			 System.out.println(x);
		
		}
		else if (y>z && y>x)
		{
			System.out.println(y);
		}
		
		else 
		{
			System.out.println(z);
		}
	}

}
