package packageone;

public class OperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5;
		int y=12;
		System.out.println(x+y);
		System.out.println(y-x);
		System.out.println(x*y);
		System.out.println(y/x);
		System.out.println(x^2);
		System.out.println(y%x);
		System.out.println(x>y);
		System.out.println(x<y);
		System.out.println(x==y);
		System.out.println(x!=y);
		System.out.println(x++);
		System.out.println(++x);
		
		int z=x==y?9:8;
		System.out.println(z);
	
	}
	
}
